package javahowto;

public class Sum_ofarray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		 int[] Array = {1, 5, 10, 25};
		    int add = 0;
		    int i; 
		      
		    
		    for (i = 0; i < 2; i++) {
		      add += Array[i];//concatination "+" whwere addition to combine strings 
		    }
		    System.out.println("The sum is: " + add);
	}

}
